        rectShape.lineTo(-rectLength / 2, rectWidth / 2);
        rectShape.lineTo(rectLength / 2, rectWidth / 2);
        rectShape.lineTo(rectLength / 2, -rectWidth / 2);
        rectShape.lineTo(-rectLength / 2, -rectWidth / 2);
